package main

import "fmt"

func main() {
	partI()
	partII()
	partIII()
}

func partI() {
	input := ""

	totalPotions := 0
	for _, c := range input {
		switch c {
		case 'B':
			totalPotions++
		case 'C':
			totalPotions += 3
		}
	}

	fmt.Println(totalPotions)

}

func partII() {
	input := ""
	totalPotions := 0
	for i := 0; i < len(input); i += 2 {
		first := input[i]
		second := input[i+1]

		additionalPotions := 0
		if first != 'x' && second != 'x' {
			additionalPotions = 1
		}

		switch first {
		case 'A':
			totalPotions += 0 + additionalPotions
		case 'B':
			totalPotions += 1 + additionalPotions
		case 'C':
			totalPotions += 3 + additionalPotions
		case 'D':
			totalPotions += 5 + additionalPotions
		}

		switch second {
		case 'A':
			totalPotions += 0 + additionalPotions
		case 'B':
			totalPotions += 1 + additionalPotions
		case 'C':
			totalPotions += 3 + additionalPotions
		case 'D':
			totalPotions += 5 + additionalPotions
		}
	}

	fmt.Println(totalPotions)
}

func partIII() {
	input := ""

	totalPotions := 0
	for i := 0; i < len(input); i += 3 {
		monsters := input[i : i+3]

		additionalPotions := 0
		for _, c := range monsters {
			if c != 'x' {
				additionalPotions++
			}
		}
		if additionalPotions > 0 {
			additionalPotions--
		}

		for _, c := range monsters {
			switch c {
			case 'A':
				totalPotions += 0 + additionalPotions
			case 'B':
				totalPotions += 1 + additionalPotions
			case 'C':
				totalPotions += 3 + additionalPotions
			case 'D':
				totalPotions += 5 + additionalPotions
			}
		}
	}
	fmt.Println(totalPotions)
}
