package main

import "fmt"

func main() {
	partI()
	partII()
	partIII()
}

func partI() {
	input := ""

	totalPotions := 0
	for _, c := range input {
		if c == 'B' {
			totalPotions++
		} else if c == 'C' {
			totalPotions += 3
		}
	}

	fmt.Println(totalPotions)

}

func partII() {
	input := ""
	totalPotions := 0
	for i := 0; i < len(input); i += 2 {
		first := input[i]
		second := input[i+1]

		additionalPotions := 0
		if first != 'x' && second != 'x' {
			additionalPotions = 1
		}

		if first == 'A' {
			totalPotions += 0 + additionalPotions
		} else if first == 'B' {
			totalPotions += 1 + additionalPotions
		} else if first == 'C' {
			totalPotions += 3 + additionalPotions
		} else if first == 'D' {
			totalPotions += 5 + additionalPotions
		}

		if second == 'A' {
			totalPotions += 0 + additionalPotions
		} else if second == 'B' {
			totalPotions += 1 + additionalPotions
		} else if second == 'C' {
			totalPotions += 3 + additionalPotions
		} else if second == 'D' {
			totalPotions += 5 + additionalPotions
		}
	}

	fmt.Println(totalPotions)
}

func partIII() {
	input := ""

	totalPotions := 0
	for i := 0; i < len(input); i += 3 {
		monsters := input[i : i+3]

		additionalPotions := 0
		for _, c := range monsters {
			if c != 'x' {
				additionalPotions++
			}
		}
		if additionalPotions > 0 {
			additionalPotions--
		}

		for _, c := range monsters {
			if c == 'A' {
				totalPotions += 0 + additionalPotions
			} else if c == 'B' {
				totalPotions += 1 + additionalPotions
			} else if c == 'C' {
				totalPotions += 3 + additionalPotions
			} else if c == 'D' {
				totalPotions += 5 + additionalPotions
			}
		}
	}
	fmt.Println(totalPotions)
}
